INSERT INTO public.article_content (article_id, article_heading, value, priority, created_at, created_by, updated_at, updated_by) VALUES ('0a0b0c3c-0d10-4cc6-8f15-5d49e41b8402', 'Zažijte Porto: Přístav šarmu s vůní vína', 'Přijměte naše pozvání na procházku křivolakými uličkami druhého největšího portugalského města. Porto vás potěší směsí francouzského šarmu, starobylých památek, příjemně inspirativní atmosférou, vůní oceánu i opojnou chutí světoznámého vína. Konec prázdnin a rané babí léto je jako stvořené pro toulky městem, které vyladí vaši duši na frekvenci pohody.',1,'2023-06-28 08:00:44.000000', 'ADMIN', null, null);
INSERT INTO public.article_content (article_id, article_heading, value, priority, created_at, created_by, updated_at, updated_by) VALUES ('0a0b0c3c-0d10-4cc6-8f15-5d49e41b8402', 'Čtvrť Ribeira: klenot s puncem UNESCO', 'O tom, že je Porto městem s jedinečnou osobností a barvitou historií se nejlépe přesvědčíte, navštívíte-li světoznámou čtvrť Ribeira. Svou prohlídku můžete začít na promenádě Cais de Ribeira, která se vine po nábřeží řeky Douro. Během procházky si užijete pohled na dokonale fotogenické vysoké domky s pestrobarevnými fasádami, poklidnou hladinu řeky i radostnou vřavu místních kaváren. Promenáda vás zavede na nevelké náměstí Praca de Ribeira, které zdobí monumentální fontána z roku 1780, celá řada kostelů, středověkých měšťanských domů, palác Casa do Infante, klášter sv. Františka, k jehož výzdobě bylo použito více než 300 kilogramů ryzího zlata, nebo Burzovní palác s dechberoucími interiéry. Obzvláště velkolepým dojmem působí bohatě zdobený Arabský sál.  ',2,'2023-06-28 08:00:44.000000', 'ADMIN', null, null);
INSERT INTO public.article_content (article_id, article_heading, value, priority, created_at, created_by, updated_at, updated_by) VALUES ('0a0b0c3c-0d10-4cc6-8f15-5d49e41b8402', 'Vila Nova de Gaia: místo zaslíbené vínu', 'Levý břeh řeky Douro je zasvěcen vínu. Hlavní turistickou atrakcí jsou desítky vinných sklípků, ve kterých dozrává slavné portské víno. Vychutnejte si sklenku portského, pocházejícího z vinic rozesetých na slunných svazích lemujících řeku Douro. Požitek z degustace ještě umocní procházka po zdejší promenádě s úchvatným výhledem na historické centrum města.',3,'2023-06-28 08:00:44.000000', 'ADMIN', null, null);
INSERT INTO public.article_content (article_id, article_heading, value, priority, created_at, created_by, updated_at, updated_by) VALUES ('2b663dcc-9a90-4cc6-a9b2-580bf7893d05', 'Speciality s vůní orientu aneb Jak chutnají Spojené arabské emiráty' ,'Sedm pohádkově krásných emirátů, které se nachází na východě Arabského poloostrova, láká do své náruče každoročně čím dál více návštěvníků. Moderní mrakodrapy, luxusní hotely, mohutné horské štíty, průzračně čisté moře, písečné duny i špičkové služby, jsou jen zlomkem toho, co můžete v této krásné zemi obdivovat. Bohatý orientální stát si libuje v získávání mnoha světových prvenství. Je libo dotknout se nebe na střeše nejvyšší budovy světa, prohlížet si umělecká díla během potápění, sjíždět na lyžích sjezdovku uprostřed nákupního centra, nebo vypravit do budoucnosti pomocí špičkových technologií? Vítejte ve světě, kde není nic nemožné.',1,'2023-06-28 08:00:44.000000', 'ADMIN', null, null);
INSERT INTO public.article_content (article_id, article_heading, value, priority, created_at, created_by, updated_at, updated_by) VALUES ('2b663dcc-9a90-4cc6-a9b2-580bf7893d05', 'Koření v hlavní roli' ,'Stejně rozmanitá je i zdejší gastronomie. V emirátech žije přes sto různých národností, a právě kulturní mix dává místním specialitám ten pravý šmrnc. Jestli je něco, co opravdu definuje zdejší jídlo, tak je to koření. Vůně kardamonu, kmínu, zázvoru, hřebíčku, drceného pepře, muškátu, šafránu a mnoha dalších se line z restaurací, pouličních bister, i z uliček zdejších tržnic. Ochutnat tu samozřejmě můžete dobroty z celého světa, ale byl by hřích nedat šanci i tradiční arabské kuchyni. A na jaké notoricky známé lahůdky s vůní orientu tu zaručeně natrefíte?',2,'2023-06-28 08:00:44.000000', 'ADMIN', null, null);
INSERT INTO public.article_content (article_id, article_heading, value, priority, created_at, created_by, updated_at, updated_by) VALUES ('2b663dcc-9a90-4cc6-a9b2-580bf7893d05', 'Hummus' ,'Variabilní arabský dip, či pomazánka, kterou miluje celý svět. Rozmixovaná vařená cizrna se ochutí pastou ze sezamu zvanou tahini a přidají se do ní další ingredience. Zpravidla česnek, sůl, olivový olej a citrónová šťáva. Jeho kolébkou je pravděpodobně území dnešního Turecka, ale raketovou rychlostí se dostal i do ostatních koutů zeměkoule. A zvláště v arabských zemích bez něho nedají ani ránu. Existuje v mnoha obměnách a servíruje se tradičně s chlebovou plackou, či syrovou zeleninou. Kromě lahodné chuti se pyšní řadou benefitů, které potěší vaši linii a celkové zdraví. Pokud jste s hummusem ještě neměli tu čest, rozhodně to napravte. Je božský.',3,'2023-06-28 08:00:44.000000', 'ADMIN', null, null);
INSERT INTO public.article_content (article_id, article_heading, value,priority, created_at, created_by, updated_at, updated_by) VALUES ('77aa739a-4dd8-4798-9088-1f2c768f1cba', 'Vydejte se do Makaronésie aneb Ostrovní ráje ve vodách Atlantiku' ,'Kdo by netoužil strávit několik dnů na ostrovech rozesetých ve vodách Atlantiku? Představte si všudypřítomnou sytou zeleň, příjemné subtropické klima, exotické pokrmy i divoké scenérie, které vás nenechají na pochybách, že to, co tyto zdánlivě rozdílné ostrovy spojuje, je jejich sopečný původ. Ať už se rozhodnete pro kterýkoli z nich, věřte, že tu narazíte na místa, která se vám navždy vryjí do paměti, zažijete pocit naprosté volnosti a v nenápadné restauraci na pobřeží vás oslní chuť pokrmů, jejichž tajnou ingrediencí je radost ze života. Přijměte pozvání do Makaronésie, která zahrnuje Kapverdy, Kanárské ostrovy, Madeiru a Azory.',1,'2023-06-28 08:00:44.000000', 'ADMIN', null, null);
INSERT INTO public.article_content (article_id, article_heading, value,priority, created_at, created_by, updated_at, updated_by) VALUES ('77aa739a-4dd8-4798-9088-1f2c768f1cba', 'Kapverdy','Nejjižněji situovanou destinací ze čtyřlístku Makaronéského souostroví jsou turisty stále oblíbenější Kapverdské ostrovy. Svou popularitu získaly nejen kvůli kráse zdejších písčitých pláží, divokým vlnám, jako stvořeným pro surfování, elegantním hotelům i malebným horským vesničkám. Půvaby a rozmanitost zdejších ostrovů okouzlily i slavného britského přírodovědce Charlese Darwina.
INSERT INTO public.article_content (article_id, article_heading, value,priority, created_at, created_by, updated_at, updated_by) VALUES ('77aa739a-4dd8-4798-9088-1f2c768f1cba', 'Kanárské ostrovy','Tenerife, Gran Canaria, Fuerteventura, Lanzarote, La Palma, La Gomera, El Hierro a Graciosa. Kdo by neznal Kanárské ostrovy – turistické ráje, kde je každé přání předem splněno. Subtropické klima s dlouhými suchými léty a mírnými zimami sem každoročně přiláká na 12 milionů turistů. Jedna z nejmalebnějších částí Atlantiku uspokojí i ty nejnáročnější cestovatele, ale zároveň si stále zachovává svůj přírodní ráz i autentickou atmosféru.
