INSERT INTO place_content (place_id, priority, content_type, value, is_active, created_at,created_by, updated_at, updated_by) VALUES ('e9c8ec42-b619-47fc-a3f1-e61f50ba603b', 4, 'FOOD','Snídaně jsou automaticky zahrnuty v ceně ubytování formou bufetu. Možnost dokoupení obědů či večeří na místě přímo v resortu. V nabídce je pravá thajská kuchyně, ale i mezinárodní pokrmy.', true,'2023-06-29 07:59:01.000000', 'ADMIN', null, null);
INSERT INTO place_content (place_id, priority, content_type, value, is_active, created_at,created_by, updated_at, updated_by) VALUES ('e9c8ec42-b619-47fc-a3f1-e61f50ba603b', 1, 'LOCATION','Royal Cliff Hotels Group se nachází přímo ve městě Pattaya u nádherné pláže. Mezinárodní letiště Bangkok je od hotelů vzdálené 135 km. Luxusní letovisko se nachází ve vzdálenosti 800 metrů od historického chrámu Wat Khao Phra Bat a pouhých 5 km od letoviska Pattaya.', true,'2023-06-29 07:59:01.000000', 'ADMIN', null, null);
INSERT INTO place_content (place_id, priority, content_type, value, is_active, created_at,created_by, updated_at, updated_by) VALUES ('e9c8ec42-b619-47fc-a3f1-e61f50ba603b', 2, 'EQUIPMENT','Recepce 24 hodin denně, Wi-Fi zdarma, gril, soukromá pláž, bazén, dětský bazén, zahrada, sluneční terasa, restaurace, bar, bar u bazénu, kavárna, snack bar, pokojová služba, směnárna, turistické informace, úschova zavazadel, bankomat, hlídání dětí, dětské hřiště, animační tým, prádelna a čistírna, čištění bot, společenské prostory, business centrum, fax/kopírka, kadeřnictví/kosmetika, obchod se suvenýry, VIP pokoje, svatební apartmá, rodinné pokoje, denní tisk, obchody v hotelu, bezbariérový přístup, výtah, pokoj pro alergiky, prostor pro kuřáky, tenisový kurt, stolní tenis, squash, minigolf, sauna, fitness centrum, lázně a wellness, pára, vířivka, masáže, pronájem kol a aut, parkování.', true,'2023-06-29 07:59:01.000000', 'ADMIN', null, null);
INSERT INTO place_content (place_id, priority, content_type, value, is_active, created_at,created_by, updated_at, updated_by) VALUES ('e9c8ec42-b619-47fc-a3f1-e61f50ba603b', 3, 'ACCOMMODATION','Royal Cliff Hotels Group nabízí ubytování v klimatizovaných pokojích a apartmánech vše moderně vybavené s thajským interiérem, pohodlným posezením a balkonem. Díky systému IPTV si hosté mohou objednávat jídlo, sledovat filmy ve vysokém rozlišení nebo si kontrolovat účty v pohodlí vlastního pokoje. Samozřejmostí je vlastní koupelna a sociální zařízení.', true,'2023-06-29 07:59:01.000000', 'ADMIN', null, null);
INSERT INTO place_content (place_id, priority, content_type, value, is_active, created_at,created_by, updated_at, updated_by) VALUES ('cc65b1e2-7e75-4e6e-a657-faa49aa9bb83', 4, 'FOOD','Snídaně jsou automaticky zahrnuty v ceně ubytování.', true,'2023-06-29 07:59:01.000000', 'ADMIN', null, null);
INSERT INTO place_content (place_id, priority, content_type, value, is_active, created_at,created_by, updated_at, updated_by) VALUES ('cc65b1e2-7e75-4e6e-a657-faa49aa9bb83', 1, 'LOCATION','Railay Great View Resort & Spa se nachází v části East Railay, na jedné z nejkrásnějších pláží na světě, v jižním Thajsku. Letiště Krabi je odtud vzdáleno cca 25 km a do centra města je to cca 15 km. Z Railay Great View Resort & Spa můžete dojet člunem za 10 minut k přístavu Ao Nam Mao.', true,'2023-06-29 07:59:01.000000', 'ADMIN', null, null);
INSERT INTO place_content (place_id, priority, content_type, value, is_active, created_at,created_by, updated_at, updated_by) VALUES ('cc65b1e2-7e75-4e6e-a657-faa49aa9bb83', 2, 'EQUIPMENT','Recepce 24 hodin denně, soukromá pláž, venkovní bazén, zahrada, terasa, restaurace, bar, Wi-Fi zdarma, pronájem aut a kol, směnárna, turistické informace, úschovna zavazadel, prádelna a čistírna, business centrum, fax/kopírka, minimarket, klimatizace, masáže, prostor pro kuřáky.', true,'2023-06-29 07:59:01.000000', 'ADMIN', null, null);
INSERT INTO place_content (place_id, priority, content_type, value, is_active, created_at,created_by, updated_at, updated_by) VALUES ('cc65b1e2-7e75-4e6e-a657-faa49aa9bb83', 3, 'ACCOMMODATION','Railay Greatview Resort nabízí ubytování v prostorných (32 – 36 m2) a jemně zdobených chatách v thajském stylu s moderním vybavením. Chat je k dispozici 42 a jsou typu Deluxe a Grand Deluxe. Chaty jsou velmi pohodlné s dřevěným nábytkem. Chaty mají vlastní balkon a krásný výhled buď na moře či do tropické zahrady. Možné je ubytování v chatě s manželskou postelí či na dvou jednolůžkových oddělených postelích. Je zde možnost dvou propojených pokojů.', true,'2023-06-29 07:59:01.000000', 'ADMIN', null, null);